import React, { useState, useEffect } from 'react';
import { jsPDF } from 'jspdf';
import './App.css';

const App: React.FC = () => {
  const [imageUrl, setImageUrl] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [favorites, setFavorites] = useState<string[]>([]);
  const [showFavorites, setShowFavorites] = useState<boolean>(false);

  const fetchRandomPuppyImage = async () => {
    setLoading(true);
    setError(null);
    try {
      let validImage = false;
      let image = '';

      while (!validImage) {
        const response = await fetch('https://dog.ceo/api/breeds/image/random');
        if (!response.ok) {
          throw new Error('Failed to fetch image');
        }
        const data = await response.json();
        image = data.message;

        // Check image dimensions
        const img = new Image();
        img.src = image;

        await new Promise((resolve) => {
          img.onload = () => {
            if (img.width >= 640 && img.width <= 1280) {
              validImage = true;
            }
            resolve(null);
          };
          img.onerror = () => resolve(null);
        });
      }

      setImageUrl(image);
    } catch (err) {
      setError('Could not fetch a puppy image. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const addToFavorites = () => {
    if (imageUrl && !favorites.includes(imageUrl)) {
      setFavorites([...favorites, imageUrl]);
    }
  };

  const toggleShowFavorites = () => {
    setShowFavorites(!showFavorites);
  };

  const exportFavoritesToPDF = () => {
    if (favorites.length === 0) {
      alert('No favorite images to export!');
      return;
    }

    const pdf = new jsPDF();
    let yPosition = 10;
    const pdfWidth = 190; // PDF width (A4 size minus margins)

    favorites.forEach((image, index) => {
      const img = new Image();
      img.src = image;

      pdf.text(`Favorite Puppy ${index + 1}`, 10, yPosition);
      yPosition += 10;

      img.onload = () => {
        const aspectRatio = img.width / img.height;
        const imgHeight = pdfWidth / aspectRatio; // Calculate height to maintain aspect ratio

        if (yPosition + imgHeight > 280) {
          // Add a new page if the image doesn't fit
          pdf.addPage();
          yPosition = 10;
        }

        pdf.addImage(img, 'JPEG', 10, yPosition, pdfWidth, imgHeight);
        yPosition += imgHeight + 10; // Add spacing after the image
      };
    });

    pdf.save('favorite-puppies.pdf');
  };

  useEffect(() => {
    fetchRandomPuppyImage();
  }, []);

  return (
    <div className="app">
      <div className="top-bar">
        <button onClick={fetchRandomPuppyImage} disabled={loading}>
          {loading ? 'Fetching...' : 'Fetch Another Puppy'}
        </button>
        <button onClick={toggleShowFavorites}>
          {showFavorites ? 'Show All Puppies' : 'Show Favorites'}
        </button>
        {!showFavorites && (
          <button className="favorite-button" onClick={addToFavorites}>
            ❤️ Add to Favorites
          </button>
        )}
        {showFavorites && (
          <button className="export-button" onClick={exportFavoritesToPDF}>
            📄 Export to PDF
          </button>
        )}
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {!showFavorites && imageUrl && !loading && (
        <div>
          <img src={imageUrl} alt="A cute puppy" className="puppy-image" />
        </div>
      )}
      {showFavorites && (
        <div className="favorites">
          <h2>Your Favorite Puppies</h2>
          {favorites.length === 0 ? (
            <p>No favorites yet!</p>
          ) : (
            favorites.map((fav, index) => (
              <img key={index} src={fav} alt={`Favorite ${index}`} className="puppy-image" />
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default App;
