import React, { useState, useEffect } from 'react';
import './App.css';

const App: React.FC = () => {
  const [imageUrl, setImageUrl] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [favorites, setFavorites] = useState<string[]>([]);
  const [showFavorites, setShowFavorites] = useState<boolean>(false);

  const fetchRandomPuppyImage = async () => {
    setLoading(true);
    setError(null);
    try {
      let validImage = false;
      let image = '';

      while (!validImage) {
        const response = await fetch('https://dog.ceo/api/breeds/image/random');
        if (!response.ok) {
          throw new Error('Failed to fetch image');
        }
        const data = await response.json();
        image = data.message;

        // Check image dimensions
        const img = new Image();
        img.src = image;

        await new Promise((resolve) => {
          img.onload = () => {
            if (img.width >= 640 && img.width <= 1280) {
              validImage = true;
            }
            resolve(null);
          };
          img.onerror = () => resolve(null);
        });
      }

      setImageUrl(image);
    } catch (err) {
      setError('Could not fetch a puppy image. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const addToFavorites = () => {
    if (imageUrl && !favorites.includes(imageUrl)) {
      setFavorites([...favorites, imageUrl]);
    }
  };

  const toggleShowFavorites = () => {
    setShowFavorites(!showFavorites);
  };

  useEffect(() => {
    fetchRandomPuppyImage();
  }, []);

  return (
    <div className="app">
      <div className="top-bar">
        <button onClick={fetchRandomPuppyImage} disabled={loading}>
          {loading ? 'Fetching...' : 'Fetch Another Puppy'}
        </button>
        <button onClick={toggleShowFavorites}>
          {showFavorites ? 'Show All Puppies' : 'Show Favorites'}
        </button>
      </div>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {!showFavorites && imageUrl && !loading && (
        <div>
          <img src={imageUrl} alt="A cute puppy" className="puppy-image" />
          <button className="favorite-button" onClick={addToFavorites}>
            ❤️ Add to Favorites
          </button>
        </div>
      )}
      {showFavorites && (
        <div className="favorites">
          <h2>Your Favorite Puppies</h2>
          {favorites.length === 0 ? (
            <p>No favorites yet!</p>
          ) : (
            favorites.map((fav, index) => (
              <img key={index} src={fav} alt={`Favorite ${index}`} className="puppy-image" />
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default App;
